mapper.select:

select A2.*
from(
	select A1.*
	from (
		select
			rownum as seq, A.*
		from
			(SELECT
				anony_Board_Id,
				anony_Board_Title,
				anony_Board_Content,
				anony_Board_ReadCnt,
				anony_Board_Create_Dt,
				anony_Board_Update_Dt
			FROM
			    anony_board
			WHERE 1=1

		    ORDER BY anony_Board_Create_Dt DESC
		   ) A
		) A1
	where seq  >  #{firstIndex}
	) A2
where rownum  <=  #{recordCountPerPage}

mapper.update:
UPDATE ANONY_REPLY
SET REPLY_CONTENT = #{reply_Content}, REPLY_UPDATE_DT = sysdate
WHERE REPLY_ID = #{reply_Id}

mapper.select:
select count(*)
from anony_reply
where anony_board_id =  #{anony_board_id}

select A2.*
from(
		select A1.*
		from (
				 select
					 rownum as seq, A.*
				 from
					 (SELECT
						  REPLY_ID,
						  REPLY_CONTENT,
						  REPLY_CREATE_DT,
						  REPLY_UPDATE_DT,
						  ANONY_BOARD_ID
					  FROM
						  anony_reply
					  WHERE 1=1 and
					      ANONY_BOARD_ID = #{anony_Board_Id}
					  ORDER BY REPLY_CREATE_DT DESC
					 ) A
			 ) A1
		where seq  >  #{firstIndex}
	) A2
where rownum  <=  #{recordCountPerPage}

SELECT  ANONY_BOARD_CREATE_DT

FROM ANONY_BOARD

WHERE ANONY_BOARD_ID = #{anony_Board_Create_Dt}
