mapper.select:
select D3.*
from(
select D2.*
from(
select rownum as seq, D1.*
from(
SELECT
reservation_notice_id
,reservation_notice_title
,emp_id
,reservation_notice_content
,reservation_notice_create_date
,reservation_notice_st
FROM
RESERVATION_NOTICE
where
reservation_notice_st = '1'


) D1
) D2
where seq  >=  #{firstIndex}
) D3
where rownum  <=  #{recordCountPerPage}

mapper.insert:

    select MEETROOM_ID_SEQ.nextval from dual

INSERT INTO meet_room (
meet_room_id
,meet_room_no
,meet_room_st
,meet_room_admin_id
,meet_room_capacity
,meet_room_name
,meet_room_attach_name
,meet_room_attach_type
,meet_room_create_date
,meet_room_attach_origin
,meet_room_content
) VALUES (
#{meet_Room_Id}
,#{meet_Room_No}
,#{meet_Room_St}
,#{meet_Room_Admin_Id}
,#{meet_Room_Capacity}
,#{meet_Room_Name}
,#{meet_Room_Attach_Name}
,#{meet_Room_Attach_Type}
,sysdate
,#{meet_Room_Attach_Origin}
,#{meet_Room_Content}
)

mapper.update:
UPDATE meet_room
SET
    meet_room_st = #{meet_Room_St}
  ,meet_room_capacity = #{meet_Room_Capacity}
  ,meet_room_name =  #{meet_Room_Name}
  ,meet_room_content = #{meet_Room_Content}
WHERE
    meet_room_id =#{meet_Room_Id}

mapper.select:
select D3.* from( select D2.* from( select rownum as seq, D1.* from( SELECT A.MEET_ROOM_ID ,C.MEET_ROOM_NAME ,A.RESERVATION_START_TIME ,A.RESERVATION_END_TIME ,A.RESERVATION_DATE ,A.MEET_ROOM_RESERVATION_ID ,A.MEET_ROOM_DETAIL ,C.MEET_ROOM_ST ,C.MEET_ROOM_NO ,C.MEET_ROOM_CAPACITY ,C.MEET_ROOM_ATTACH_ORIGIN ,a.emp_id ,B.emp_name ,B.emp_picture ,B.code_name as official_Name ,B.dept_name ,B.team_name ,B.dept_id FROM MEET_ROOM_RESERVATION A LEFT OUTER JOIN ( SELECT D1.emp_id ,D1.emp_name ,D1.emp_picture ,D1.code_id ,D2.code_name ,D3.dept_name ,D3.dept_id
,D4.dept_name team_Name FROM emp D1, code D2, dept D3, dept D4 WHERE D1.code_id = D2.code_id AND D1.dept_id = D3.dept_id AND D1.dept_id = D4.dept_id(+) ORDER BY D1.code_id DESC ) B ON A.emp_id = B.emp_id , MEET_ROOM C where A.MEET_ROOM_ID= C.MEET_ROOM_ID
and A.meet_room_dept_id = #{meet_Room_Dept_Id} and MEET_ROOM_RESERVATION_ST = 1
and A.emp_id = #{emp_Id} and MEET_ROOM_RESERVATION_ST = 1
order by A.RESERVATION_DATE desc, A.RESERVATION_START_TIME
) D1 ) D2 where seq >= #{firstIndex} ) D3 where rownum <= #{recordCountPerPage}

mapper.insert:

    select MEETROOM_RESERVATION_ID.nextval from dual

INSERT INTO meet_room_reservation (
meet_room_reservation_id
, meet_room_id
, emp_id
, reservation_start_time
, reservation_end_time
, reservation_date
, meet_room_detail
, meet_room_dept_id
, MEET_ROOM_RESERVATION_ST
) VALUES (
#{meet_Room_Reservation_Id}
, #{meet_Room_Id}
, #{emp_Id}
, #{reservation_Start_Time}
, #{reservation_End_Time}
, #{reservation_Date}
, #{meet_Room_Detail}
, #{meet_Room_Dept_Id}
, 1
)

mapper.select:
select D3.*
from(
select D2.*
from(
