from(
SELECT
complain_id
, complain_title
, complain_content
, complain_st
, emp_id
, meet_room_id
, complain_create_date
FROM
reservation_complain
where
complain_st = 1


        and emp_id = #{emp_Id}


        and emp_id = #{emp_Id}




) D1
) D2
where seq  >=  #{firstIndex}
) D3
where rownum  <=  #{recordCountPerPage}

