mapper.insert:
INSERT INTO notice_attach (
							   attach_id
							 ,attach_path
							 ,attach_name
							 ,attach_type
							 ,attach_create_dt
							 ,notice_id
) VALUES (
		   notice_attach_seq.nextval
		 ,#{attach_path}
		 ,#{attach_name}
		 ,#{attach_type}
		 ,sysdate
		 ,#{notice_id}
		 )

mapper.update:
UPDATE notice
SET
notice_title = #{notice_title}

	,notice_content = #{notice_content}

,notice_update_dt = sysdate
WHERE
notice_id = #{notice_id}

mapper.delete:
DELETE FROM notice_attach
WHERE
	notice_id = #{notice_id}
  AND attach_id = #{attach_id}
