mapper.insert:

    SELECT SCHEDULE_ID_SEQ.NEXTVAL FROM DUAL

insert into SCHEDULE (
      SCHEDULE_ID
    ,SCHEDULE_TITLE
    ,SCHEDULE_CONTENT
    ,SCHEDULE_CREATE_DT
    ,SCHEDULE_START_DT
    ,SCHEDULE_END_DT
    ,SCHEDULE_IMP
    ,SCHEDULE_ST
    ,SCHEDULE_LOCATION
    ,SCHEDULE_WRITER_ID
    ,CODE_ID
    ,DEPT_ID
) VALUES (
          #{schedule_Id}
        ,#{schedule_Title}
        ,#{schedule_Content}
,SYSDATE
,to_date(#{schedule_Start_Dt},'yyyy-MM-ddHH24:mi')
,to_date(#{schedule_End_Dt},'yyyy-MM-ddHH24:mi')
,#{schedule_Imp}
,1
,#{schedule_Location}
,#{schedule_Writer_Id}
    ,#{code_Id}

    ,#{dept_Id}


    ,#{team_Id}

)

mapper.update:
UPDATE  SCHEDULE


        SCHEDULE_TITLE = #{schedule_Title}


        ,SCHEDULE_CONTENT = #{schedule_Content},

    SCHEDULE_START_DT = to_date(#{schedule_Start_Dt},'yyyy-MM-ddHH24:mi'),
    SCHEDULE_END_DT = to_date(#{schedule_End_Dt},'yyyy-MM-ddHH24:mi'),

        SCHEDULE_IMP = #{schedule_Imp},


        SCHEDULE_ST = #{schedule_St},


        SCHEDULE_LOCATION = #{schedule_Location},


WHERE   SCHEDULE_ID = #{schedule_Id}
AND		SCHEDULE_WRITER_ID = #{schedule_Writer_Id}

UPDATE schedule

    SCHEDULE_ST = 0

WHERE
SCHEDULE_ID =  #{schedule_Id}
AND		SCHEDULE_WRITER_ID = #{schedule_Writer_Id}
