mapper.select:
SELECT APPROVAL_TITLE, APPROVAL_CONTENT, APPROVAL_RECOMMAND_DT, APPROVAL_TERMINATION_DT, APPROVAL_ST, EMP_DRAFTER_ID, DOC_ID, APPROVAL_CANCEL_REASON
FROM APPROVAL
WHERE DOC_ID = #{doc_Id}

SELECT ATTACH_ID, ATTACH_PATH, ATTACH_NAME, ATTACH_ORIGIN, ATTACH_CREATE_DT, ATTACH_END_DT, DOC_ID, ATTACH_TYPE
FROM APPROVAL_ATTACH
WHERE DOC_ID = #{doc_Id}

SELECT D2.*
FROM (SELECT D1.*
FROM( SELECT ROWNUM as seq, D.*
FROM ( SELECT A.APPROVAL_TITLE, A.APPROVAL_RECOMMAND_DT, A.APPROVAL_ST, A.DOC_ID, C.FORM_NAME
FROM APPROVAL A, APPROVAL_ATTACH D, APPROVAL_FORM C
WHERE A.FORM_ID = C.FORM_ID AND A.DOC_ID = D.DOC_ID(+) AND APPROVAL_ST NOT IN(4,5) AND EMP_DRAFTER_ID = #{emp_Drafter_Id}
GROUP BY A.APPROVAL_TITLE, A.APPROVAL_RECOMMAND_DT, A.APPROVAL_ST, A.EMP_DRAFTER_ID, A.DOC_ID, C.FORM_NAME
ORDER BY 2 DESC
)D
)D1
where seq >= #{firstIndex}
)D2
where rownum <= #{recordCountPerPage}

mapper.update:
update approval

set APPROVAL_END_DT = #{endDt}, APPROVAL_START_DT = #{startDt}


where doc_Id = #{doc_Id}

UPDATE APPROVAL_HISTORY

SET HISTORY_ST = #{history_st},
    HISTORY_ALLOW_DT = current_Date,
    HISTORY_TURN = #{history_Turn}

WHERE DOC_ID = #{doc_Id} and EMP_ID = #{emp_Id}

mapper.select:
SELECT EMP_ID
FROM APPROVAL_HISTORY
WHERE HISTORY_TURN  = 'Y' AND DOC_ID = #{doc_Id}

mapper.update:
UPDATE APPROVAL_HISTORY
SET HISTORY_TURN = #{history_Turn}
WHERE DOC_ID = #{doc_Id} and HISTORY_STEP = #{history_Step}

UPDATE APPROVAL

SET APPROVAL_CANCEL_REASON = #{approval_Cancel_Reason},
    APPROVAL_CONTENT = #{approval_Content},
    APPROVAL_ST = #{approval_St}

WHERE DOC_ID = #{doc_Id}

mapper.select:
SELECT HISTORY_STEP

FROM APPROVAL_HISTORY


WHERE EMP_ID = #{emp_Id} AND DOC_ID = #{doc_Id}

select form_id, form_name, form_content, form_sup_id
from approval_form
where form_id = #{form_id}

